using System.Linq;
using Xunit;

namespace ReedMullerCode.UnitTests
{
    public class DecoderUnitTests
    {
        [Theory]
        [InlineData(6, "1111000011110000111100001111000011110000111100001111000011110000", "1001000")]
        [InlineData(10, "", "10010000101")]
        [InlineData(12, "", "1001111010101")]
        public void Decode_Vector_Succeeds(int m, string encodedVector, string expectedDecodedVector)
        {
            var decoder = new Decoder.Decoder(m);

            var decodedVector = decoder.Decode(encodedVector.ToList().Select(c => int.Parse(c.ToString())).ToList());

            var decodedVectorString = string.Join("", decodedVector.Select(bit => bit.ToString()).ToList());

            Assert.Equal(expectedDecodedVector, decodedVectorString);
        }
    }
}